package alac

import (
	"bytes"
	"encoding/hex"
	"testing"
)

func Test(t *testing.T) {
	a, err := New()
	if err != nil {
		t.Fatal(err)
	}

	for enc, dec := range map[string]string{
		// 2 channel cases
		"":                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   "",
		"": "",
		"200000040013080981f8c1ff80000013080981f8c1ff800000ff80afbfe02bfc": "",
		"": "",
		"":                                                                                                                                                                                                                                                                                         "",
		"":                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       "",
	} {
		encB, err := hex.DecodeString(enc)
		if err != nil {
			t.Fatal(err)
		}
		decB, err := hex.DecodeString(dec)
		if err != nil {
			t.Fatal(err)
		}

		if have, want := a.Decode(encB), decB; !bytes.Equal(have, want) {
			t.Errorf("have\n  %x\nwant\n  %x\n", have, want)
		}
	}
}
